package cn.tk.java.io.commonsIo.image.thumbnailator;

import java.io.File;
import java.io.IOException;

import org.apache.commons.io.FileUtils;
import org.junit.Test;

public class ZTest {
	@Test
	public void testResize() throws InterruptedException
	{
		String imagePath = "D:\\MyDocuments\\lijl85\\桌面\\index.png";
		String outputPath = "D:\\MyDocuments\\lijl85\\桌面\\indexCopy.png";
		String strImg = "data:image/png;base64,";
		ThumbnailUtil.generateImage(strImg, imagePath);
		ThumbnailUtil.resize(imagePath, outputPath, 100, 51, "png");
		String encoderImage = ThumbnailUtil.encoderImage(imagePath);
		Thread removeThread = new Thread(new Runnable() {
			@Override
			public void run() {
				try {
					Thread.sleep(3000);
					FileUtils.forceDelete(new File(imagePath));
					FileUtils.forceDelete(new File(outputPath));
				} catch (Exception e) {
					e.printStackTrace();
				}
			}
		});
		removeThread.start();
		System.out.println(encoderImage);
	}

	@Test
	public void testDelete() throws IOException
	{
		String imagePath = "D:\\MyDocuments\\lijl85\\桌面\\index.png";
		String outputPath = "D:\\MyDocuments\\lijl85\\桌面\\indexCopy.png";
		FileUtils.forceDelete(new File(imagePath));
		FileUtils.forceDelete(new File(outputPath));
	}
}
